canvas.addShape('marker', {
  attrs: {
    ...commonAttrs,
    x: 200,
    y: 100,
    symbol: 'square',
  },
});

canvas.addShape('marker', {
  attrs: {
    ...commonAttrs,
    x: 300,
    y: 100,
    symbol: 'diamond',
  },
});

canvas.addShape('marker', {
  attrs: {
    ...commonAttrs,
    x: 400,
    y: 100,
    symbol: 'triangle',
  },
});

canvas.addShape('marker', {
  attrs: {
    ...commonAttrs,
    x: 500,
    y: 100,
    symbol: 'triangle-down',
  },
