    circle.attr('fill', color);
  });
  circleFolder.addColor(circleConfig, 'stroke').onChange(function (color) {
    circle.attr('stroke', color);
  });
  circleFolder.add(circleConfig, 'lineWidth', 1, 20).onChange(function (lineWidth) {
    circle.attr('lineWidth', lineWidth);
  });
  circleFolder.add(circleConfig, 'fillOpacity', 0, 1, 0.1).onChange(function (opacity) {
    circle.attr('fillOpacity', opacity);
  });
  circleFolder.add(circleConfig, 'strokeOpacity', 0, 1, 0.1).onChange(function (opacity) {
    circle.attr('strokeOpacity', opacity);
  });
  circleFolder.open();
});

