    circleList.push(circle);
  }

  circleList.forEach(function (circle, i) {
    circle.animate(function (ratio) {
      return {
        x: 300 + 100 * Math.cos(ratio * Math.PI * 2 + i / 6 * Math.PI * 2),
        y: 200 + 100 * Math.sin(ratio * Math.PI * 2 + i / 6 * Math.PI * 2)
      };
    }, {
      duration: 3000,
      repeat: true
    });
  });
});

